import React, { useState } from 'react';

const AddStudent = () => {
  const [student, setStudent] = useState({ name: '', standard: '' });

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Student submitted:', student);
    alert('Student added (check console)');
    setStudent({ name: '', standard: '' });
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name: </label>
          <input type="text" name="name" value={student.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Standard: </label>
          <input type="text" name="standard" value={student.standard} onChange={handleChange} required />
        </div>
        <button type="submit">Add Student</button>
      </form>
    </div>
  );
};

export default AddStudent;
